import React, {useEffect, useState} from 'react'
import "./WeatherComponent.css";
import WeatherChart from '../WeatherChart/WeatherChart';
import { getWeather } from '../api/api';


const WeatherComponent = () => {
  const [weather, setWeather] = useState(null)

  useEffect(() => {

    const getWeatherData = async() => {
      const dataFromServer = await getWeather('Kyiv')

      setWeather(dataFromServer);
      console.log(weather)
    }

    getWeatherData()
  }, []);

  return (
    <div className="container">
      <div className="forecast-card">
        <div className="forecat__header">
          <div className="forecasr__left">
            <div className="forecast__city">
              Cherkasy, UA
            </div>
            <div className="forecast__data">
              Cherkasy, UA
            </div>
          </div>
          <div className="forecast__right">
          <div className="forecast__description">
             Sunny
            </div>
          </div>
        </div>
        <WeatherChart />
      </div>

    </div>
  )
}

export default WeatherComponent
