const ICON_API = "https://openweathermap.org/img/wn/"

export const getIcon = async(iconCode) => {
  const iconFromServer = await fetch(`${ICON_API}${iconCode}@2x.png`)
  const responceFromServer = await iconFromServer.json();

  // console.log('Responce icon: ', responceFromServer)
  return responceFromServer.data || responceFromServer;
}
